import React from "react";

function PopupWithForm(props) {

  // // закрытие попапа кликом на esc
  // function handleEscClose (evt) {
  //   if (evt.key === 'Escape') {
  //     {props.closeAllPopups};
  //   }
  // }

  return (
    <div className={`popup popup-${props.name} ${props.isEditProfilePopupOpen ||
    props.isAddPlacePopupOpen || props.isEditAvatarPopupOpen ? 'popup_opened' : ''}`}
         onClick={props.closeAllPopups}>
      <div className="popup__container">
        <button type="button" className="popup__close-button"
                onClick={props.closeAllPopups}>
        </button>
        <form name={`${props.name}`} className="popup__form" noValidate onSubmit={props.onSubmit}>
          <h2 className="popup__title">{props.title}</h2>
          {props.children}
        </form>
      </div>
    </div>
  );
}

export default PopupWithForm;
